import React, { useEffect, useState } from "react";
import "./App.css";
import "chart.js";

function App() {
  const [chatData, setChatData] = useState([]);

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: "top",
      },
      title: {
        display: true,
        text: "지난 7일 간 채팅 건수",
      },
    },
  };

  const data = {
    labels: ["January", "February", "March", "April", "May", "June", "July"],
    datasets: [
      {
        type: "line",
        label: "Dataset 1",
        borderColor: "rgb(54, 162, 235)",
        borderWidth: 2,
        data: [1, 2, 3, 4, 5],
      },
      {
        type: "bar",
        label: "Dataset 2",
        backgroundColor: "rgb(255, 99, 132)",
        data: [1, 2, 3, 4, 5, 6],
        borderColor: "red",
        borderWidth: 2,
      },
      {
        type: "bar",
        label: "Dataset 3",
        backgroundColor: "rgb(75, 192, 192)",
        data: [1, 2, 3, 4, 5, 6],
      },
    ],
  };

  const Chart = () => {
    return (
      <Container>
        <Chart data={data} />
      </Container>
    );
  };
}

const Container = styled.div`
  width: 90vw;
  max-width: 900px;
`;

export default App;
